import { React } from "react";
import Carousel from "react-elastic-carousel";
import "./RowSlider.css";
function RowSlider(props) {
  function fun(mov) {
    return (
      <div className="card">
        <div className="images_card_overlay">
          <div className="card_title">{mov.name}</div>
        </div>

        <img
          className="images_card"
          src={"https://image.tmdb.org/t/p/original" + mov.backdrop_path}
          alt=""
        />
      </div>
    );
  }
  const breakPoints = [
    { width: 1, itemsToShow: 1 },
    { width: 400, itemsToShow: 2 },
    { width: 600, itemsToShow: 3 },
    { width: 800, itemsToShow: 4 },
    { width: 1000, itemsToShow: 5 },
    { width: 1200, itemsToShow: 6 },
  ];
  const movie = props.movie;
  console.log(movie);
  return (
    <div>
      <br />
      <h1 className="popular_h1">popular movies</h1>
      {/* <div className="container"> */}
      <Carousel
        focusOnSelect="true"
        // breakPoints={breakPoints}
        enableMouseSwipe="true"
      >
        {movie.length > 0 && movie.map(fun)}
      </Carousel>
      {/* </div> */}
    </div>
  );
}

export default RowSlider;
