// }

function fun(val) {
  return (
    <div>
      <img src={val.img} height="200px" width="100%t"></img>
    </div>
  );
}

const breakpoints = [
  {
    breakpoint: 500,
    settings: {
      slidesToShow: 2,
      slidesToScroll: 2,
    },
  },
  {
    breakpoint: 768,
    settings: {
      slidesToShow: 3,
      slidesToScroll: 3,
    },
  },
];

function Rowbox() {
  // return <div class="container">{NDatas.map(fun)}</div>;
  // return <Carousel breakPoints={breakPoints}>{NDatas.map(fun)}</Carousel>;

  return (
    <InfiniteCarousel
      breakpoints={breakpoints}
      dots={true}
      showSides={true}
      sidesOpacity={0.5}
      sideSize={0.1}
      slidesToScroll={4}
      slidesToShow={4}
      scrollOnDevice={true}


    >
      <div>{NDatas.map(fun)}</div>
    </InfiniteCarousel>
  );
}

export default Rowbox;
