import React from "react";
import NDatas from "./NData";
import "./row.css";
import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css";

// const breakPoints = [
//   // { width: 1, itemsToShow: 1 },
//   // { width: 600, itemsToShow: 2 },
//   // { width: 808, itemsToShow: 3 },
//   // { width: 1200, itemsToShow: 4 },
//   { width: 1, itemsToShow: 1 },
//   { width: 550, itemsToShow: 2, itemsToScroll: 2, pagination: false },
//   { width: 850, itemsToShow: 3 },
//   { width: 1150, itemsToShow: 4, itemsToScroll: 2 },
//   { width: 1450, itemsToShow: 5 },
//   { width: 1750, itemsToShow: 6 },
// ];

// function fun(val) {
//   return (
//     <div className="card">
//       <img src={val.img}></img>
//     </div>
//   );

//   // return <Card img={val.img} title={val.title} link={val.link} />;
// }
const responsive = {
  superLargeDesktop: {
    // the naming can be any, depends on you.
    breakpoint: { max: 4000, min: 3000 },
    items: 5,
  },
  desktop: {
    breakpoint: { max: 3000, min: 1024 },
    items: 3,
  },
  tablet: {
    breakpoint: { max: 1024, min: 464 },
    items: 2,
  },
  mobile: {
    breakpoint: { max: 464, min: 0 },
    items: 1,
  },
};
function fun(val) {
  return <img src={val.img}></img>;
}

const CustomRightArrow = ({ onClick, ...rest }) => {
  const {
    onMove,
    carouselState: { currentSlide, deviceType },
  } = rest;
  // onMove means if dragging or swiping in progress.
  return <button onClick={() => onClick()} />;
};
function Rowbox() {
  // return <div class="container">{NDatas.map(fun)}</div>;
  // return <Carousel breakPoints={breakPoints}>{NDatas.map(fun)}</Carousel>;

  return (
    <Carousel
      responsive={responsive}
      swipeable={true}
      draggable={true}
      showDots={true}
      responsive={responsive}
      ssr={true} // means to render carousel on server-side.
      infinite={true}
      autoPlaySpeed={1000}
    >
      <div>{NDatas.map(fun)}</div>
    </Carousel>
  );
}

export default Rowbox;
