import React, { useEffect, useState } from "react";
import "./Slider.css";
import { BsPlayFill } from "react-icons/bs";
import requests from "./Requests";
import RowSlider from "./RowSlider";
const rand = Math.floor(Math.random() * 10);
function Slider() {
  const [movie, setMovie] = useState([]);
  const [back_img, setBackImg] = useState("");
  const [ban_name,setBanName] = useState("");
  const [ban_dec,setBanDes] = useState("");

  // console.log(rand);
  // console.log(back_img);

  // const fetchNetflixOriginals =
  //   "https://api.themoviedb.org/3/discover/tv?api_key=a3c1a584d8679e42019c3e8b71d1d539&with_networks=213";
  useEffect(() => {
    fetch(requests.fetchTrending)
      .then((res) => res.json())
      .then((data) => {
        setMovie(data.results);
        setBackImg(
          "https://image.tmdb.org/t/p/original" + data.results[rand].backdrop_path
        );
        setBanName(
           data.results[rand].name
        );
        setBanDes(
          data.results[rand].overview
        );


      });
  }, []);

   console.log(movie[rand], "hello");
  return (
    <div>
      <header
        className="banner_header"
        style={{
          backgroundSize: "cover",
          backgroundImage: `url(${back_img})`,
          backgroundPosition: "center center ",
        }}
      >
        {/* <img src={back_img} alt=""/> */}
        <div className="banner__contents">
          <h1 className="banner__name">
            {ban_name>0 && ban_name}
          </h1>
          <div className="banner__buttons">
            <button className="banner__but">
              <div className="banner__button__icon">
                <BsPlayFill />
              </div>
              Play
            </button>
            <button className="banner__but">More info</button>
          </div>
          <div className="banner__des">
            <div className="banner_des12">{ban_dec.length>0 &&ban_dec.substring(0,100)+" ..."}
            </div>
          </div>
        </div>
      </header>
      <RowSlider movie={movie} />
    </div>
  );
}

export default Slider;
