import React from 'react';
import './Slider.css';
import { BiPlay, IconName } from "react-icons/bi";
function Slider() {
    return (<div>
        <header className="banner_header" style={{
            backgroundImage:`url("https://www.themoviedb.org/t/p/original/1R68vl3d5s86JsS2NPjl8UoMqIS.jpg")`,
            backgroundSize: "contain",
            backgroundPosition: "center center"
        }}>

        <div className="banner_contents">
            <h1 className='banner_name'>

                Hawkeye
            </h1>
            <div className="banner_buttons">
            <button className="banner_but"><div className="banner_button_icon"><BiPlay/></div>Play</button>
            <button className="banner_but">add to List</button>
            </div>
            <div className="banner_des">
                this is the banner descripton. The movies are
                based on the genres
            </div>
        </div>
        </header>
    </div>);
}

export default Slider;
