import React, { useEffect, useState } from "react";
import "./nav.css";

import { Link } from "react-router-dom";
function NavBar() {
  const [show, showHandler] = useState(false);
  const downSlide = () => {
    if (window.scrollY >=80) {
      showHandler(true);
    } else {
      showHandler(false);
    }
  };
  window.addEventListener('scroll',downSlide)
  // useEffect(() => {
  //   window.addEventListener("scroll", showHandler);
  //   return () => {
  //     window.removeEventListener("scroll", showHandler);
  //   };
  // }, []);
  return (

     <div className={`nav ${show && "nav_black"}`}>
     <div className="nav_contents">
        <Link to="/"><img
          className="nav_logo"
          src="http://assets.stickpng.com/images/580b57fcd9996e24bc43c529.png"
          alt=""
        ></img></Link>
        <div className="nav_list">
          <div className="nav_list_objects">
          <a href="" >Home</a>
          <a href="">TV SHows</a>
          <a href="">Movies</a>
          <a href="">New Popular</a>
          <a href="">My List</a>
        </div>
        </div>
        <img
          className="nav_avator"
          src="https://upload.wikimedia.org/wikipedia/commons/0/0b/Netflix-avatar.png"
          alt=""
        ></img>
      </div> 
     </div>
  );
}

export default NavBar;
