import React, { useEffect, useState } from "react";
import "./Slider.css";
import { IconName, BsPlayFill } from "react-icons/bs";
import axios from "axios";
import requests from './Requests' 

function Slider() {
  const [movie, setMovie] = useState([]);

  useEffect(() => {
    async function fetchData() {
      const request = await axios.get(requests.fetchNetflixOriginals);
      setMovie(
        request.data.results[
          Math.floor(Math.random * requests.data.results.length - 1)
        ]
      );
      return request;
    }
    fetchData();
  }, []);

  return (
    <div>
      <header
        className="banner_header"
        style={{
          backgroundImage: `url("https://www.themoviedb.org/t/p/original/1R68vl3d5s86JsS2NPjl8UoMqIS.jpg")`,
          backgroundPosition: "center center",
        }}
      >
        <div className="banner_contents">
          <h1 className="banner_name">Hawkeye</h1>
          <div className="banner_buttons">
            <button className="banner_but">
              <div className="banner_button_icon">
                <BsPlayFill />
              </div>
              Play
            </button>
            <button className="banner_but">More info</button>
          </div>
          <div className="banner_des">
            this is the banner descripton. The movies are based on the genres
          </div>
        </div>
      </header>
    </div>
  );
}

export default Slider;
