import React from 'react';
import './Slider.css';
function Slider() {
    return (<div>
        <header className="banner_header" style={{
            backgroundImage:`url("https://www.themoviedb.org/t/p/w600_and_h900_bestv2/w21lgYIi9GeUH5dO8l3B9ARZbCB.jpg")`,
            backgroundSize: "cover",
            backgroundPosition: "center center"
        }}>

        <div className="banner_contents">
            <h1 className='banner_name'>
                Movie
            </h1>
            <div className="banner_buttons">
            <button>play</button>
            <button>add to List</button>
            </div>
            <div className="banner_des">
                this is the banner descripton. The movies are
                based on the genres
            </div>
        </div>
        </header>
    </div>);
}

export default Slider;
