import React from "react";
import Slider from "./Slider";
import InfiniteCarousel from 'react-leaf-carousel';
import "./RowSlider.css";
function RowSlider(props) {
  function fun(mov) {
    return (
      <div >
        <div>
        {
          <img
            className="cards"
            src={"https://image.tmdb.org/t/p/original" + mov.backdrop_path}
            alt=""
          />
        }
        </div>
      </div>
    );
  }
  const movie = props.movie;
  console.log(movie);
  return (
    <div>
        <InfiniteCarousel
         breakpoints={[
            {
              breakpoint: 500,
              settings: {
                slidesToShow: 2,
                slidesToScroll: 2,
              },
            },
            {
              breakpoint: 768,
              settings: {
                slidesToShow: 3,
                slidesToScroll: 3,
              },
            },
          ]}
          dots={true}
          showSides={true}
          sidesOpacity={.5}
          sideSize={.1}
          slidesToScroll={4}
          slidesToShow={4}
          scrollOnDevice={true}

        >
      <div  className="row_box">{movie.map(fun)}</div>
      </InfiniteCarousel>
    </div>
  );
}

export default RowSlider;
