import { React, useState } from "react";
import Carousel from "react-elastic-carousel";
import { BsFillPlayCircleFill } from "react-icons/bs";
import { BiLike, BiDislike } from "react-icons/bi";
import { AiOutlinePlusCircle } from "react-icons/ai";
import { AiTwotoneLike } from "react-icons/ai";
import "./RowSlider.css";
function RowSlider(props) {
  const movie = props.movie;

  function fun(mov) {
    var islike = false;
    console.log(mov.id, "hello");
    const mov_id = mov.id;
    const likeButton =
      islike === false ? (
        <BiLike className="play_icon_like"></BiLike>
      ) : (
        <AiTwotoneLike className="play_icon_like"></AiTwotoneLike>
      );

    const butClick = () => {
      islike = !islike;
    };

    const adul = mov.adult === "false" ? "16+" : "18+";
    const mov_type = mov.media_type ? mov.media_type : "movie";
    //console.log(mov, "mov");

    const called = () => {
      console.log(mov.id);
      let flag = 0;
      props.watchlist.filter((element) => {
        if (element.id == mov_id) {
          flag = 1;
        }
      });
      // if (props.watchlist[id].includes(mov_id)===false) {
      if (flag == 0) {
        const nt = [...props.watchlist, mov];
        props.addToWatchlist(nt);
      }

      // }
      //console.log(nt, "nt");
    };

    function fun1(mov) {
      var islike = false;
      console.log(mov.id, "hello");
      const mov_id = mov.id;
      const likeButton =
        islike === false ? (
          <BiLike className="play_icon_like"></BiLike>
        ) : (
          <AiTwotoneLike className="play_icon_like"></AiTwotoneLike>
        );

      const butClick = () => {
        islike = !islike;
      };

      const adul = mov.adult === "false" ? "16+" : "18+";
      const mov_type = mov.media_type ? mov.media_type : "movie";
      //console.log(mov, "mov");

      const called = () => {
        console.log(mov.id);
        let flag = 0;
        props.watchlist.filter((element) => {
          if (element.id == mov_id) {
            flag = 1;
          }
        });
        // if (props.watchlist[id].includes(mov_id)===false) {
        if (flag == 0) {
          const nt = [...props.watchlist, mov];
          props.addToWatchlist(nt);
        }

        // }
        //console.log(nt, "nt");
      };

      function fun(mov) {
        var islike = false;
        console.log(mov.id, "hello");
        const mov_id = mov.id;
        const likeButton =
          islike === false ? (
            <BiLike className="play_icon_like"></BiLike>
          ) : (
            <AiTwotoneLike className="play_icon_like"></AiTwotoneLike>
          );

        const butClick = () => {
          islike = !islike;
        };

        const adul = mov.adult === "false" ? "16+" : "18+";
        const mov_type = mov.media_type ? mov.media_type : "movie";
        //console.log(mov, "mov");

        const called = () => {
          console.log(mov.id);
          let flag = 0;
          props.watchlist.filter((element) => {
            if (element.id == mov_id) {
              flag = 1;
            }
          });
          // if (props.watchlist[id].includes(mov_id)===false) {
          if (flag == 0) {
            const nt = [...props.watchlist, mov];
            props.addToWatchlist(nt);
          }

          // }
          //console.log(nt, "nt");
        };

        return (
          <div className="container">
            <img
              className="image"
              src={"https://image.tmdb.org/t/p/original" + mov.poster_path}
              alt=""
            />
            <div className="overlay">
              <div className="text">
                {mov.name}
                {mov.title}
                <div>
                  <BsFillPlayCircleFill className="play_icon"></BsFillPlayCircleFill>
                  <AiOutlinePlusCircle
                    onClick={called}
                    className="play_icon circle"
                  ></AiOutlinePlusCircle>
                  <span onClick={butClick}>{likeButton}</span>
                  <BiDislike className="play_icon_like"></BiDislike>
                </div>
                <div>
                  {adul} {mov_type} HD
                </div>
              </div>
              <div></div>
            </div>
          </div>
        );
      }
      const breakPoints = [
        { width: 1, itemsToShow: 1 },
        { width: 400, itemsToShow: 2 },
        { width: 600, itemsToShow: 4 },
        { width: 900, itemsToShow: 5 },
        { width: 1200, itemsToShow: 6 },
        { width: 1400, itemsToShow: 8 },
      ];
      return (
        <div>
          if ({props.iswatchlist})
          {
            <div>
              <br />
              <h1 className="popular_h1">{props.name}</h1>
              {/* <div className="container"> */}
              <Carousel
                itemPosition="START"
                renderPagination={({ pages, activePage, onClick }) => {
                  return <div></div>;
                }}
                itemPadding={[10, 5]}
                // itemsToShow={5}

                breakPoints={breakPoints}
                // enableMouseSwipe="true"
              >
                {movie.length > 0 && movie.map(fun1)}
              </Carousel>
              {/* </div> */}
            </div>
          }
          else
          {
            <div>
              <br />
              <h1 className="popular_h1">{props.name}</h1>
              {/* <div className="container"> */}
              <Carousel
                itemPosition="START"
                renderPagination={({ pages, activePage, onClick }) => {
                  return (
                    // <Flex direction="row">
                    //   {pages.map(page => {
                    //     const isActivePage = activePage === page
                    //     return (
                    //       <Square
                    //         key={page}
                    //         onClick={() => onClick(page)}
                    //         active={isActivePage}
                    //       />
                    //     )
                    //   })}
                    // </Flex>
                    <div></div>
                  );
                }}
                itemPadding={[10, 5]}
                // itemsToShow={5}

                breakPoints={breakPoints}
                // enableMouseSwipe="true"
              >
                {movie.length > 0 && movie.map(fun)}
              </Carousel>
              {/* </div> */}
            </div>
          }
        </div>
      );
    }
  }
}

export default RowSlider;
