import React, { useEffect, useState } from "react";
import "./Slider.css";
import { BsPlayFill } from "react-icons/bs";
import axios from "./axios";
import requests from "./Requests";

function Slider() {
  const [movie, setMovie] = useState([]);

  // const fetchNetflixOriginals =
  //   "https://api.themoviedb.org/3/discover/tv?api_key=a3c1a584d8679e42019c3e8b71d1d539&with_networks=213";
  useEffect(() => {
    //   fetch(requests.fetchNetflixOriginals)
    //     .then((res) => res.json())
    //     .then((data) => {
    //       console.log(data);
    //       setMovie(data.results);
    //     });
    async function fetchData() {
      const req = await axios.get(requests.fetchComedyMovies);
      console.log(req, "hello");
      setMovie(
        req.data[console.log(Math.floor(Math.random() * req.data.length - 1))]
      );
      return req;
    }
    fetchData();
  }, []);
  // console.log(movie, "dfdf");

  return (
    <div>
      <header
        className="banner_header"
        style={{
          backgroundImage: `url("https://image.tmdb.org/t/p/original/${movie.results?.backdrop_path}"})`,
          backgroundPosition: "center center",
        }}
      >
        <div className="banner_contents">
          <h1 className="banner_name">Hawkeye</h1>
          <div className="banner_buttons">
            <button className="banner_but">
              <div className="banner_button_icon">
                <BsPlayFill />
              </div>
              Play
            </button>
            <button className="banner_but">More info</button>
          </div>
          <div className="banner_des">
            this is the banner descripton. The movies are based on the genres
          </div>
        </div>
      </header>
    </div>
  );
}

export default Slider;
